mapper.update:
UPDATE  JEW_BANNER
    SET

         BANNER_TYPE    =#{bannerType}
        ,BANNER_NM      =#{bannerNm}
        ,BANNER_MEMO    =#{bannerMemo}
        ,BANNER_TXT     =#{bannerTxt}
        ,BANNER_IMAGE   =#{bannerImage}
        ,SORT_ORDR      =#{sortOrdr}
        ,STR_DT         =REPLACE(#{strDt},"-","")
        ,END_DT         =REPLACE(#{endDt},"-","")
        ,LINK_TYPE      =#{linkType}
        ,LINK_URL       =#{linkUrl}
        ,USE_YN         =#{useYn}
        ,MOD_ID         =#{modId}
        ,MOD_DT         =NOW()
  WHERE 1=1
  and BANNER_SNO =#{bannerSno}
