mapper.select:
SELECT JTI.THEMA_GROUP_ID ,A.THEMA_GROUP_NM ,JTI.THEMA_ID ,JTI.THEMA_NM ,JTI.THEMA_TITLE ,JTI.THEMA_MEMO ,JTI.THEMA_ETC ,JTI.TBL_ID ,JTI.LOAD_GUBUN ,JTI.LOAD_TERM ,JTI.COLLECT_TYPE ,FN_CODE_NM('JEW001',JTI.COLLECT_TYPE) as COLLECT_TYPE_NM ,JTI.COLLECT_CYCLE ,FN_CODE_NM('JEW002',JTI.COLLECT_CYCLE) as COLLECT_CYCLE_NM ,JTI.API_URL ,JTI.HTML_FILE ,JTI.ORDER_CNT ,JTI.USE_YN ,JTI.DELETE_YN ,JTI.REG_DT ,JTI.REG_ID ,JTI.MOD_DT ,JTI.MOD_ID FROM JEW_THEMA_INFO JTI ,JEW_THEMA_GROUP A WHERE 1=1 AND JTI.THEMA_GROUP_ID =A.THEMA_GROUP_ID AND JTI.DELETE_YN = 'N'
AND JTI.THEMA_GROUP_ID = #{themaGroupId}
AND JTI.THEMA_ID LIKE CONCAT('%', #{searchKeyword}, '%')
AND JTI.THEMA_NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND JTI.USE_YN = #{useYn}
order by JTI.ORDER_CNT asc

mapper.update:
UPDATE JEW_THEMA_INFO SET
     THEMA_GROUP_ID = #{themaGroupId}
    ,THEMA_ID = #{themaId}
    ,THEMA_NM = #{themaNm}
    ,THEMA_TITLE = #{themaTitle}
    ,THEMA_MEMO = #{themaMemo}
    ,THEMA_ETC = #{themaEtc}
    ,TBL_ID = #{tblId}
    ,LOAD_GUBUN = #{loadGubun}
    ,LOAD_TERM = #{loadTerm}
    ,COLLECT_TYPE = #{collectType}
    ,COLLECT_CYCLE = #{collectCycle}
    ,API_URL = #{apiUrl}
    ,HTML_FILE = #{htmlFile}
    ,ORDER_CNT = #{orderCnt}
    ,USE_YN = #{useYn}
    ,MOD_DT = NOW()
    ,MOD_ID = #{modId}
WHERE THEMA_GROUP_ID = #{themaGroupId}
