mapper.select:
select b.SITE_MEMU_NM ,b.SITE_MEMU_TILE ,a.cnt from (
select SITE_ID ,SITE_URL ,count(*) as cnt from INFO_SITE_VISIT a where 1=1 AND DATE_FORMAT(A.V_DATE,'%Y%m%d') BETWEEN CONCAT(DATE_FORMAT(SYSDATE(),'%Y'),'0101') AND CONCAT(DATE_FORMAT(SYSDATE(),'%Y'),'1231') and a.PAGE_TYPE not in('main') group by SITE_ID ,SITE_URL
)a ,( select a.URL ,a.SITE_MEMU_NM ,a.SITE_MEMU_TILE ,b.MENU_GROUP_ID ,b.MENU_GROUP_NM ,b.SITE_ID from info_site_menu a ,info_site_menu_group b where 1=1 and a.MENU_TYPE not in('top') and a.MENU_GROUP_ID =b.MENU_GROUP_ID
) b where 1=1 and a.SITE_URL =b.url and a.SITE_ID =b.SITE_ID and a.SITE_ID =#{siteId} order by cnt desc
